CREATE TABLE IF NOT EXISTS `usuarios` (
      `id` INT(11) UNSIGNED NOT NULL AUTO_INCREMENT,
      `nome` VARCHAR( 50 ) NOT NULL ,
      `usuario` VARCHAR( 25 ) NOT NULL ,
      `senha` VARCHAR( 40 ) NOT NULL ,
      `email` VARCHAR( 100 ) NOT NULL ,
      `nivel` INT(1) UNSIGNED NOT NULL DEFAULT '1',
      `cadastro` DATETIME NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP,
      `twitch` VARCHAR(50) NOT NULL,
      PRIMARY KEY (`id`),
      UNIQUE KEY `usuario` (`usuario`),
      KEY `nivel` (`nivel`));

INSERT INTO usuarios (nome, usuario, senha, email, nivel) VALUES ('administrador', 'admin', SHA1('admin'), 'admin@admin', 4);

CREATE TABLE IF NOT EXISTS `publicacao` (
      `id` INT(11) UNSIGNED NOT NULL AUTO_INCREMENT,
      `titulo` VARCHAR(50) NOT NULL,
      `categoria` VARCHAR(50) NOT NULL,
      `autor` VARCHAR(26) NOT NULL,
      `postagem` DATETIME NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP,
      `conteudo` text NOT NULL,
      `imagem` text NOT NULL,
      PRIMARY KEY (`id`));

CREATE TABLE IF NOT EXISTS `comentario` (
      `autor` VARCHAR(26) NOT NULL,
      `postagem` DATETIME NOT NULL DEFAULT CURRENT_TIMESTAMP ON UPDATE CURRENT_TIMESTAMP,
      `conteudo` text NOT NULL,
      `id` INT(11) UNSIGNED NOT NULL AUTO_INCREMENT,
      PRIMARY KEY (`id`),
      FOREIGN KEY ( `id` ) REFERENCES `publicacao`( `id` ));

CREATE TABLE IF NOT EXISTS `favoritos` (
  `user_id` INT(11) UNSIGNED NOT NULL AUTO_INCREMENT,
  `publi_id` INT(11) UNSIGNED NOT NULL AUTO_INCREMENT,
  PRIMARY KEY (`user_id`, `publi_id`),
  FOREIGN KEY (`user_id`) REFERENCES `usuarios`(`id`),
  FOREIGN KEY (`movie_id`) REFERENCES `publicacao`(`id`));

INSERT INTO `publicacao` (`titulo`, `categoria`, `autor`, `postagem`, `conteudo`, `imagem`) VALUES
(' Boas-vindas!', ' Tecnologias em Geral', ' rickzinho', '2022-03-18 13:42:53', ' <p>Ol&aacute;! Nos somos os nerds amadores, sejam bem-vindos e aproveitem!</p> ', ' app/controllers/images/carousel/Nerds Amadores.png'),
(' Bitcoin estabiliza acima de US$ 40 mil, abre espaço para Ethereum e altcoins ganham terreno após decisão do Fomc', ' Cripto', ' rickzinho', '2022-03-18 13:50:19', ' <p id=\"isPasted\">O Bitcoin (BTC) se estabilizou acima de US$ 40 mil logo ap&oacute;s reagir negativamente assim que o Federal Reserve (Fed) anunciou o primeiro aumento da taxa de juros dos EUA desde 2018, de 0,25 ponto percentual, para um intervalo entre 0,25% e 0,5%.</p><p>Logo ap&oacute;s o comunicado do Fed, a criptomoeda caiu mais de 3%, de US$ 41 mil para cerca de US$ 39.600, mas logo retomou e apagou as perdas ainda na tarde de ontem. Nesta manh&atilde;, &eacute; negociado a US$ 40.552,71, em alta de 0,6% nas &uacute;ltimas 24 horas.</p><p id=\"isPasted\">O movimento pode se sustentar a partir daqui. Michael Safai, s&oacute;cio-gerente da empresa de negocia&ccedil;&atilde;o quantitativa Dexterity Capital, acredita que o aumento dos juros em 25 pontos-base j&aacute; estava precificado. &ldquo;Sab&iacute;amos que essa taxa estava chegando h&aacute; muito tempo. Os traders de criptomoedas s&atilde;o inteligentes&rdquo;.</p><p>A expectativa de valoriza&ccedil;&atilde;o da criptomoeda no curto prazo tamb&eacute;m &eacute; apoiada pela queda de liquidez da Coinbase, muito usada por empresas para comprar BTC. Na semana passada, a exchange registrou retirada l&iacute;quida de 31.130 bitcoins (US$ 1,2 bilh&atilde;o), o maior n&uacute;mero semanal desde 2017.</p><p>Na pr&aacute;tica, a queda no saldo de Bitcoin dispon&iacute;vel na corretora indica que menos criptomoedas est&atilde;o dispon&iacute;veis para liquida&ccedil;&atilde;o, favorecendo a alta de pre&ccedil;os mesmo que a demanda se mantenha. &nbsp;Criptomoedas removidas de exchanges s&atilde;o, em geral, levadas para carteiras frias, desconectadas da Internet e basicamente inativas.</p><p>Traders j&aacute; vinham se posicionando em op&ccedil;&otilde;es de compra (calls) para se proteger contra o risco de uma alta repentina de pre&ccedil;os. Segundo dados da ferramenta Skew, o mercado de op&ccedil;&otilde;es colocou uma probabilidade de 52% de que o BTC seja negociado acima de US$ 38 mil em abril.</p><p id=\"isPasted\">A manuten&ccedil;&atilde;o do BTC no patamar atual tamb&eacute;m abre espa&ccedil;o para diversas altcoins, puxadas pela alta de 2,8% do Ethereum (ETH), a segunda maior cripto por valor de mercado.</p><p>Entre os ativos menores, o destaque vai para a Xido Finance (XIDO), que dispara 26,5% hoje e acumula valoriza&ccedil;&atilde;o de mais de 80% na semana. O ativo alimenta uma plataforma de finan&ccedil;as descentralizadas (DeFi) que oferece renda passiva em cripto sem precisar emitir tokens a todo momento, como normalmente acontece com solu&ccedil;&otilde;es rivais.</p><p>Outro criptoativo que dispara 20% &eacute; o THORChain (RUNE). O projeto lan&ccedil;ou recentemente uma plataforma de negocia&ccedil;&atilde;o de tokens que representam criptomoedas famosas como Bitcoin e Ethereum, mas na blockchain Cosmos (ATOM).</p><p>A proposta &eacute; similar &agrave; da Aave (AAVE), uma das maiores plataformas DeFi de empr&eacute;stimos, que sobe 13% hoje ap&oacute;s uma atualiza&ccedil;&atilde;o que libera trocas de criptomoedas entre diferentes blockchains (mais informa&ccedil;&otilde;es abaixo).</p><p>No entanto, os ganhos de altcoins podem ser limitados pela alta correla&ccedil;&atilde;o com o Bitcoin, que est&aacute; pr&oacute;xima do maior n&iacute;vel de todos os tempos. Isso significa que, ao contr&aacute;rio do que aconteceu durante boa parte do ano passado, o desempenho das alts est&aacute; sob forte depend&ecirc;ncia dos movimentos do BTC.</p><p>O n&iacute;vel atual de correla&ccedil;&atilde;o &ldquo;pinta um quadro geral de avers&atilde;o ao risco no mercado&rdquo;, escreveu a Arcane Research em um relat&oacute;rio recente.</p><p>Segundo a casa de an&aacute;lise, traders veem semelhan&ccedil;as com julho do ano passado, quando as criptos alternativas derreteram em meio &agrave; corre&ccedil;&atilde;o do Bitcoin. Por isso, diz a Arcane, h&aacute; menos desejo entre investidores de se posicionar em altcoins, o que indica que o BTC pode continuar tendo melhor desempenho relativo.</p><p>O sentimento j&aacute; aparece no mercado de op&ccedil;&otilde;es de Ethereum, com traders comprando op&ccedil;&otilde;es de venda (puts) da criptomoeda, afirmou a QCP Capital em relat&oacute;rio.</p> ', ' app/controllers/images/carousel/imagem_2022-03-18_134354.png'),
(' Gols de Palmeiras x Corinthians pelo Paulistão serão transformados em NFT', ' Cripto', ' rickzinho', '2022-03-18 13:52:17', ' <p id=\"isPasted\">O derby de hoje entre Palmeiras e Corinthians, pelo Campeonato Paulista de futebol, ser&aacute; o primeiro do Brasil a ter gols transformados em NFT em tempo real.</p><p>Os NFTs estar&atilde;o dispon&iacute;veis em formato de v&iacute;deo colecion&aacute;vel logo ap&oacute;s o acontecimento de cada gol e receber&atilde;o envelopamento com anima&ccedil;&otilde;es no formato 3D.</p><p>Com tecnologia do app iSportistics e equipe de edi&ccedil;&atilde;o de v&iacute;deo dedicada, os gols ser&atilde;o disponibilizados imediatamente para venda no marketplace de NFTs da Binance, patrocinadora Master da competi&ccedil;&atilde;o.</p><p>Os gols ficar&atilde;o dispon&iacute;veis por at&eacute; 24h em formato leil&atilde;o e o vencedor do leil&atilde;o de cada gol ainda receber&aacute; uma bola Penalty oficial customizada utilizada no confronto.</p> ', ' app/controllers/images/carousel/imagem_2022-03-18_135203.png'),
(' GeForce RTX 3050 e 3090 Ti: Nvidia apresenta novas placas de vídeo na CES 2022', ' Hardware', ' rickzinho', '2022-03-18 13:54:40', ' <div data-block-id=\"2\" data-block-type=\"unstyled\" data-block-weight=\"67\" id=\"isPasted\" style=\"box-sizing: inherit; margin: 0px auto 2rem; padding: 0px 1rem; border: 0px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-weight: 400; font-stretch: inherit; font-size: 1.25rem; line-height: 2rem; font-family: opensans, helvetica, arial, sans-serif; vertical-align: initial; max-width: 42.5rem; width: 680px; float: none; letter-spacing: -0.03125rem; overflow-wrap: break-word; color: rgb(51, 51, 51); orphans: 2; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255); text-decoration-thickness: initial; text-decoration-style: initial; text-decoration-color: initial;\"><p data-track-category=\"Link no Texto\" data-track-links=\"\" style=\"text-align: left;\">A NVIDIA apresentou as novas placas de v&iacute;deo GeForce RTX 3050 e a 3090 Ti nesta ter&ccedil;a-feira (4), durante confer&ecirc;ncia virtual na CES 2022, feira de tecnologia que acontece em Las Vegas, EUA. Os dispositivos s&atilde;o modelos de entrada e top de linha, respectivamente, e t&ecirc;m 8 GB e 24 GB de mem&oacute;ria. A empresa tamb&eacute;m apresentou novidades para o p&uacute;blico gamer, al&eacute;m de tecnologias automobil&iacute;sticas.</p></div><div style=\"box-sizing: inherit; margin: 0px; padding: 0px; border: 0px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-weight: 400; font-stretch: inherit; font-size: 18px; line-height: inherit; font-family: opensans, helvetica, arial, sans-serif; vertical-align: initial; color: rgb(51, 51, 51); letter-spacing: normal; orphans: 2; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255); text-decoration-thickness: initial; text-decoration-style: initial; text-decoration-color: initial;\"><div data-block-id=\"3\" data-block-type=\"unstyled\" data-block-weight=\"65\"><p data-track-category=\"Link no Texto\" data-track-links=\"\" style=\"box-sizing: inherit; margin: 0px; padding: 0px; border: 0px; font: inherit; vertical-align: initial; text-align: left;\">A GeForce RTX 3050 ter&aacute; seu lan&ccedil;amento oficial em 27 de janeiro e vai custar a partir de 250 d&oacute;lares (cerca de R$ 1.412 em convers&atilde;o direta). De acordo com Jeff Fisher, vice-presidente s&ecirc;nior da NVIDIA para a divis&atilde;o de GeForce, a nova placa de entrada atinge velocidades de at&eacute; 60 FPS. Ainda n&atilde;o h&aacute; informa&ccedil;&otilde;es sobre in&iacute;cio das vendas ou pre&ccedil;os no Brasil.</p><p data-track-category=\"Link no Texto\" data-track-links=\"\" style=\"box-sizing: inherit; margin: 0px; padding: 0px; border: 0px; font: inherit; vertical-align: initial; text-align: left;\"><br></p><p data-track-category=\"Link no Texto\" data-track-links=\"\" style=\"box-sizing: inherit; margin: 0px; padding: 0px; border: 0px; font: inherit; vertical-align: initial; text-align: left;\"><br></p><div data-block-id=\"7\" data-block-type=\"unstyled\" data-block-weight=\"67\" id=\"isPasted\" style=\"box-sizing: inherit; margin: 0px auto 2rem; padding: 0px 1rem; border: 0px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-weight: 400; font-stretch: inherit; font-size: 1.25rem; line-height: 2rem; font-family: opensans, helvetica, arial, sans-serif; vertical-align: initial; max-width: 42.5rem; width: 680px; float: none; letter-spacing: -0.03125rem; overflow-wrap: break-word; color: rgb(51, 51, 51); orphans: 2; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255); text-decoration-thickness: initial; text-decoration-style: initial; text-decoration-color: initial;\"><p data-track-category=\"Link no Texto\" data-track-links=\"\" style=\"box-sizing: inherit; margin: 0px; padding: 0px; border: 0px; font: inherit; vertical-align: initial; text-align: left;\">Na confer&ecirc;ncia online da CES 2022, a NVIDIA apresentou uma nova placa de v&iacute;deo de entrada, a RTX 3050, que depois de chegar aos laptops, agora aparece em vers&atilde;o para computadores de mesa. O dispositivo chega ao mercado ainda em janeiro de 2022 e traz 8 GB de VRAM GDDR6, 2.560 n&uacute;cleos CUDA e clock de mem&oacute;ria de 14 Gbps por valores a partir dos US$ 250.</p><p data-track-category=\"Link no Texto\" data-track-links=\"\" style=\"box-sizing: inherit; margin: 0px; padding: 0px; border: 0px; font: inherit; vertical-align: initial; text-align: left;\"><br></p><p data-track-category=\"Link no Texto\" data-track-links=\"\" style=\"box-sizing: inherit; margin: 0px; padding: 0px; border: 0px; font: inherit; vertical-align: initial; text-align: left;\"><span id=\"isPasted\" style=\"color: rgb(51, 51, 51); font-family: opensans, helvetica, arial, sans-serif; font-size: 20px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: -0.5px; orphans: 2; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255); text-decoration-thickness: initial; text-decoration-style: initial; text-decoration-color: initial; display: inline !important; float: none;\">Em um anuncio r&aacute;pido e sem muitos detalhes, a fabricante apresentou tamb&eacute;m a sua nova placa top de linha, a RTX 3090 Ti. O modelo chega ao mercado com 10.752 n&uacute;cleos CUDA, tendo ainda 24 GB de VRAM GDDR6X e clock de at&eacute; 1.860 MHz. Com promessa de ganho de cerca de 7% em rela&ccedil;&atilde;o ao modelo RTX 3090, a nova RTX 3090 Ti deve ter mais novidades e pre&ccedil;o oficial revelado no final do m&ecirc;s de janeiro.</span></p></div><div data-block-id=\"9\" data-block-type=\"backstage-photo\" style=\"box-sizing: inherit; margin: 2.5rem auto; padding: 0px; border: 0px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-variant-numeric: inherit; font-variant-east-asian: inherit; font-weight: 400; font-stretch: inherit; font-size: 18px; line-height: inherit; font-family: opensans, helvetica, arial, sans-serif; vertical-align: initial; max-width: 85rem; clear: both; color: rgb(51, 51, 51); letter-spacing: normal; orphans: 2; text-align: start; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255); text-decoration-thickness: initial; text-decoration-style: initial; text-decoration-color: initial;\"><div style=\"box-sizing: inherit; margin: 0px auto; padding: 0px 1rem; border: 0px; font: inherit; vertical-align: initial; max-width: 42.5rem; width: 680px; float: none;\"><div style=\"box-sizing: inherit; margin: 0px; padding: 333.922px 0px 0px; border: 0px; font: inherit; vertical-align: initial; --skeleton-beam:linear-gradient(90deg,rgba(255,255,255,0) 0,rgba(255,255,255,0.7) 50%,rgba(255,255,255,0) 100%); width: var(--skeleton-width,100px); min-height: var(--skeleton-height,100px); background-color: rgb(238, 238, 238); background-image: var(--skeleton-beam); background-size: 95% 100%; background-repeat: no-repeat; background-position: 1669.07% 0px; animation: 1s linear 0s infinite normal none running bg-skeleton-box; position: relative; --skeleton-width: 100%; --skeleton-height: auto;\"></div></div></div></div></div> ', ' app/controllers/images/carousel/imagem_2022-03-18_135300.png'),
(' Ryzen com a tecnologia AMD 3D V-Cache é lançado no mercado', ' Hardware', ' rickzinho', '2022-03-18 13:55:46', ' <p id=\"isPasted\">A AMD (NASDAQ:AMD) anunciou o pre&ccedil;o e a disponibilidade do processador AMD Ryzen&trade; 7 5800X3D. Na vanguarda da inova&ccedil;&atilde;o e desempenho em jogos, o processador AMD Ryzen 7 5800X3D &eacute; o primeiro processador Ryzen a apresentar a tecnologia AMD 3D V-Cache&trade;. Com essa tecnologia l&iacute;der do setor, o 5800X3D oferece 15% mais desempenho em jogos em compara&ccedil;&atilde;o com processadores sem tecnologia de cache empilhado&sup1;, tornando-o o processador de jogos para desktop mais avan&ccedil;ado do mundo&sup2;. A AMD tamb&eacute;m est&aacute; anunciando uma nova gama de processadores Ryzen 7, 5 e 3 e compatibilidade de chipset expandida, oferecendo aos entusiastas de PC ainda mais op&ccedil;&otilde;es para criar experi&ecirc;ncias de jogo verdadeiramente personalizadas.</p><p>&ldquo;Se voc&ecirc; &eacute; um early adopter que busca o m&aacute;ximo em desempenho de jogos com o primeiro processador Ryzen com tecnologia AMD 3D V-Cache ou um novo jogador construindo seu primeiro equipamento, voc&ecirc; pode encontrar a melhor experi&ecirc;ncia da categoria com a AMD&rdquo;, explica Saeid Moshkelani, vice-presidente s&ecirc;nior e gerente geral da unidade de neg&oacute;cios Client da AMD. &ldquo;Com o an&uacute;ncio, a AMD est&aacute; oferecendo aos usu&aacute;rios o poder de escolha, trazendo desempenho de lideran&ccedil;a em jogos para todos os n&iacute;veis de constru&ccedil;&atilde;o de sistemas.&rdquo;</p><p id=\"isPasted\">Pre&ccedil;o e disponibilidade do AMD Ryzen 7 5800X3D<br style=\"box-sizing: border-box;\">Na CES 2022, a AMD anunciou o processador Ryzen 7 5800X3D, um processador de 8 n&uacute;cleos que &eacute; o primeiro a apresentar a tecnologia 3D V-Cache da AMD, oferecendo os jogos 1080p mais r&aacute;pidos em t&iacute;tulos selecionados quando comparados a outros no mercado&sup3;. A AMD anunciou que este novo processador estar&aacute; dispon&iacute;vel globalmente a partir de 20 de abril, a partir de um pre&ccedil;o inicial estimado de US$ 449.</p><p>Novos processadores de desktop AMD Ryzen convencionais<br style=\"box-sizing: border-box;\">A AMD est&aacute; lan&ccedil;ando uma s&eacute;rie de novos processadores para desktop, aproveitando o poder estabelecido das arquiteturas de n&uacute;cleo &ldquo;Zen 3&rdquo; e &ldquo;Zen 2&rdquo;. Esses processadores convencionais s&atilde;o a solu&ccedil;&atilde;o ideal para novas vers&otilde;es de PC, oferecendo at&eacute; oito n&uacute;cleos e 16 threads e 36 MB de cache no processador AMD Ryzen 7 5700X. Todos os novos processadores de desktop Ryzen 5 e Ryzen 3 anunciados hoje v&ecirc;m com um cooler AMD Wraith Stealth e devem estar dispon&iacute;veis a partir de 4 de abril.</p> ', ' app/controllers/images/carousel/imagem_2022-03-18_135526.png'),
(' O que é uma linguagem de programação?', ' Software', ' rickzinho', '2022-03-18 14:03:18', ' <p style=\"text-align: left;\"><span id=\"isPasted\" style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">N&atilde;o h&aacute; d&uacute;vidas que o computador faz parte do dia a dia da popula&ccedil;&atilde;o mundial, desde estudantes at&eacute; profissionais que utilizam a m&aacute;quinas de formas variadas para as mais diversas atividades. O mesmo n&atilde;o pode ser falado do funcionamento t&eacute;cnico desses aparelhos, com t&oacute;picos como linguagem de programa&ccedil;&atilde;o sendo inc&oacute;gnitas para o grande p&uacute;blico.</span><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">&nbsp;</span></p><p style=\"text-align: left;\"><span id=\"isPasted\" style=\"color: rgb(34, 34, 34); font-family: Arial, Helvetica, sans-serif; font-size: 14px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; orphans: 2; text-align: left; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255); text-decoration-thickness: initial; text-decoration-style: initial; text-decoration-color: initial; float: none; display: inline !important;\">N&atilde;o falamos somente de saber programar. Existe um desconhecimento geral sobre o que s&atilde;o linguagens de programa&ccedil;&atilde;o, como elas funcionam e como elas permitem que as mais diversas fun&ccedil;&otilde;es possam ser executadas por um computador.</span><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">&nbsp;</span></p><p style=\"text-align: left;\"><span id=\"isPasted\" style=\"color: rgb(34, 34, 34); font-family: Arial, Helvetica, sans-serif; font-size: 14px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; orphans: 2; text-align: left; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255); text-decoration-thickness: initial; text-decoration-style: initial; text-decoration-color: initial; float: none; display: inline !important;\">A descri&ccedil;&atilde;o b&aacute;sica de linguagem de programa&ccedil;&atilde;o &eacute; que elas s&atilde;o um conjunto de instru&ccedil;&otilde;es que desenvolvedores utilizam para criar softwares. J&aacute; a explica&ccedil;&atilde;o mais complexa explica que essas instru&ccedil;&otilde;es, quando s&atilde;o interpretadas por um computador, s&atilde;o convertidas para o chamado c&oacute;digo de m&aacute;quina, em que o dispositivo ent&atilde;o interpreta dentro de seu escopo tecnol&oacute;gico o que o programa pode fazer.</span><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">&nbsp;</span></p><p style=\"text-align: left;\"><span id=\"isPasted\" style=\"color: rgb(34, 34, 34); font-family: Arial, Helvetica, sans-serif; font-size: 14px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; orphans: 2; text-align: left; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255); text-decoration-thickness: initial; text-decoration-style: initial; text-decoration-color: initial; float: none; display: inline !important;\">Se vista pelo olhar humano ent&atilde;o, o contr&aacute;rio acontece: linguagens de programa&ccedil;&atilde;o s&atilde;o formas de desenvolvedores escreverem de forma compreens&iacute;vel para eles fun&ccedil;&otilde;es de c&oacute;digo de m&aacute;quina. &Eacute; como voc&ecirc; ler uma vers&atilde;o traduzida para portugu&ecirc;s de um livro que a l&iacute;ngua original voc&ecirc; n&atilde;o entende. Mas, assim como a obra do exemplo precisa de um tradutor para chegar ao idioma do Brasil, o mesmo ocorre com as linguagens de programa&ccedil;&atilde;o na hora de serem interpretadas no computador.</span><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">&nbsp;</span></p><p style=\"text-align: left;\"><span id=\"isPasted\" style=\"color: rgb(34, 34, 34); font-family: Arial, Helvetica, sans-serif; font-size: 14px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; orphans: 2; text-align: left; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255); text-decoration-thickness: initial; text-decoration-style: initial; text-decoration-color: initial; float: none; display: inline !important;\">Essa &ldquo;tradu&ccedil;&atilde;o&rdquo; &eacute; feita pelos chamados compiladores, ferramentas presentes no dia a dia de desenvolvedores que tem como principal fun&ccedil;&atilde;o transformar as linhas de c&oacute;digos feitas nas linguagens de programa&ccedil;&atilde;o em dados que podem ser lidos por computadores.</span><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">&nbsp;</span></p><p style=\"text-align: left;\"><span id=\"isPasted\" style=\"color: rgb(34, 34, 34); font-family: Arial, Helvetica, sans-serif; font-size: 14px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; orphans: 2; text-align: left; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255); text-decoration-thickness: initial; text-decoration-style: initial; text-decoration-color: initial; float: none; display: inline !important;\">A descri&ccedil;&atilde;o acima define linguagens de programa&ccedil;&atilde;o em geral, mas &eacute; de conhecimento p&uacute;blico a grande variedade de c&oacute;digos que existem no mundo.</span><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">&nbsp;</span></p><p style=\"text-align: left;\"><span id=\"isPasted\" style=\"color: rgb(34, 34, 34); font-family: Arial, Helvetica, sans-serif; font-size: 14px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; orphans: 2; text-align: left; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255); text-decoration-thickness: initial; text-decoration-style: initial; text-decoration-color: initial; float: none; display: inline !important;\">Essas variedades, mesmo tendo o mesmo objetivo de qualquer outra linguagem de programa&ccedil;&atilde;o, foram criadas para atender certas necessidades, seja de nichos espec&iacute;ficos de desenvolvedores ou mesmo facilitar o trabalho de cria&ccedil;&atilde;o de softwares.</span><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">&nbsp;</span></p><p style=\"text-align: left;\"><span id=\"isPasted\" style=\"color: rgb(34, 34, 34); font-family: Arial, Helvetica, sans-serif; font-size: 14px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; orphans: 2; text-align: left; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255); text-decoration-thickness: initial; text-decoration-style: initial; text-decoration-color: initial; float: none; display: inline !important;\">Com isso, o conjunto de regras que permite a constru&ccedil;&atilde;o de um programa muda de uma linguagem para outra &mdash; com diferen&ccedil;as de sintaxes e gram&aacute;ticas, termos reservados (utilizados para uma fun&ccedil;&atilde;o espec&iacute;fica no escopo da linguagem) &uacute;nicos e at&eacute; mesmo vantagens e desvantagens dependendo do objetivo que o desenvolvedor queira alcan&ccedil;ar no software.</span><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">&nbsp;</span></p><p style=\"text-align: left;\"><span id=\"isPasted\" style=\"color: rgb(34, 34, 34); font-family: Arial, Helvetica, sans-serif; font-size: 14px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; orphans: 2; text-align: left; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255); text-decoration-thickness: initial; text-decoration-style: initial; text-decoration-color: initial; float: none; display: inline !important;\">O c&oacute;digo de m&aacute;quina pode ser escrito por pessoas, mas &eacute; um processo extremamente complexo de ser aprendido. Com as linguagens de programa&ccedil;&atilde;o, ent&atilde;o, desenvolvedores conseguem ter uma sintaxe mais tang&iacute;vel e que permite eles criarem as aplica&ccedil;&otilde;es que querem sem precisar entender como se comunicar diretamente com o processador dos computadores.</span><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">&nbsp;</span></p><p style=\"text-align: left;\"><span id=\"isPasted\" style=\"color: rgb(34, 34, 34); font-family: Arial, Helvetica, sans-serif; font-size: 14px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; orphans: 2; text-align: left; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255); text-decoration-thickness: initial; text-decoration-style: initial; text-decoration-color: initial; float: none; display: inline !important;\">Al&eacute;m disso, as linguagens de programa&ccedil;&atilde;o tamb&eacute;m ajudam no desenvolvimento de aplica&ccedil;&otilde;es para m&uacute;ltiplas plataformas, a partir do uso de compiladores especificados para cada sistema. A partir disso, por exemplo, uma mesma aplica&ccedil;&atilde;o de Android pode ser compilada para o Windows com poucas altera&ccedil;&otilde;es na sintaxe da vers&atilde;o original feita para celulares.</span><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">&nbsp;</span></p><p style=\"text-align: left;\"><span id=\"isPasted\" style=\"color: rgb(34, 34, 34); font-family: Arial, Helvetica, sans-serif; font-size: 14px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; orphans: 2; text-align: left; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255); text-decoration-thickness: initial; text-decoration-style: initial; text-decoration-color: initial; float: none; display: inline !important;\">Ao mesmo tempo, as linguagens de programa&ccedil;&atilde;o tamb&eacute;m contam com algumas desvantagens. Uma das principais &eacute; que dependendo do compilador utilizado, o programa pode ser convertido para c&oacute;digo de m&aacute;quina em uma escrita n&atilde;o t&atilde;o otimizada, o que pode afetar o desempenho do computador por ele estar gastando mais recursos para entender a fun&ccedil;&atilde;o que o software precisa que ele execute.</span><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">&nbsp;</span></p><p style=\"text-align: left;\"><span id=\"isPasted\" style=\"color: rgb(34, 34, 34); font-family: Arial, Helvetica, sans-serif; font-size: 14px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; orphans: 2; text-align: left; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255); text-decoration-thickness: initial; text-decoration-style: initial; text-decoration-color: initial; float: none; display: inline !important;\">Outra desvantagem &eacute; que com o uso de linguagens de programa&ccedil;&atilde;o, desenvolvedores n&atilde;o tem controle t&atilde;o grande do computador quanto teriam caso escrevessem direto em c&oacute;digo de m&aacute;quina &mdash; que permite a configura&ccedil;&atilde;o mais espec&iacute;fica do desempenho da m&aacute;quina, como maior aloca&ccedil;&atilde;o de mem&oacute;ria para certas fun&ccedil;&otilde;es de um software, por exemplo.</span><span style=\"font-family: Arial, Helvetica, sans-serif; font-size: 14px;\">&nbsp;</span></p><p style=\"text-align: left;\"><span id=\"isPasted\" style=\"color: rgb(34, 34, 34); font-family: Arial, Helvetica, sans-serif; font-size: 14px; font-style: normal; font-variant-ligatures: normal; font-variant-caps: normal; font-weight: 400; letter-spacing: normal; orphans: 2; text-align: left; text-indent: 0px; text-transform: none; white-space: normal; widows: 2; word-spacing: 0px; -webkit-text-stroke-width: 0px; background-color: rgb(255, 255, 255); text-decoration-thickness: initial; text-decoration-style: initial; text-decoration-color: initial; float: none; display: inline !important;\">Por&eacute;m, essas duas desvantagens acabam sendo ignoradas em boa parte do mercado, pelo fato da facilidade de programar em linguagens de programa&ccedil;&atilde;o do que diretamente em c&oacute;digo de m&aacute;quina &mdash; fato que &eacute; vis&iacute;vel tamb&eacute;m no mercado de desenvolvimento de softwares, em que as vagas, em sua grande maioria, buscam por profissionais que sabem manipular c&oacute;digos como C e JavaScript, por exemplo.</span> </p> ', ' app/controllers/images/carousel/imagem_2022-03-18_135641.png'),
(' Apex Legends Mobile inicia pré-registro no Brasil', ' Tecnologias em Geral', ' rickzinho', '2022-03-18 14:06:27', ' <p><span style=\"font-size: 18px;\">Apex Legends Mobile deu in&iacute;cio ao seu pr&eacute;-registro para download em celular Android no Brasil, nesta quinta-feira (17). Os jogadores que se cadastrarem antecipadamente ser&atilde;o avisados quando o game for lan&ccedil;ado, ainda sem previs&atilde;o no pa&iacute;s, e ganhar&atilde;o itens cosm&eacute;ticos. A vers&atilde;o limitada do game j&aacute; foi disponibilizada em alguns pa&iacute;ses no final de fevereiro e &eacute; um pouco diferente do jogo nos consoles e PC.&nbsp;</span></p><p><span style=\"font-size: 18px;\">Apex Legends Mobile&nbsp;&eacute; um t&iacute;tulo independente da franquia original, com personagens e modos exclusivos, al&eacute;m de um sistema de controle pensado para tela de toque. Para realizar o pr&eacute;-registro em celulares&nbsp;Android, basta acessar a p&aacute;gina de&nbsp;Apex Legends Mobile&nbsp;na&nbsp;Google Play Store. Apesar de ainda n&atilde;o ter inscri&ccedil;&atilde;o antecipada, o game tamb&eacute;m ser&aacute; lan&ccedil;ado para&nbsp;iPhone&nbsp;(iOS).&nbsp;</span></p><p><span style=\"font-size: 18px;\">O popular Battle Royale da&nbsp;Electronic Arts&nbsp;e&nbsp;Respawn Games&nbsp;coloca jogadores para se enfrentarem em trios de campe&otilde;es chamados &quot;Lendas&quot;, cada qual com habilidades especiais. Uma equipe &eacute; lan&ccedil;ada em um grande mapa sem equipamentos e precisa encontrar armas para enfrentar os outros advers&aacute;rios at&eacute; ser o &uacute;ltimo esquadr&atilde;o sobrevivente. Na adapta&ccedil;&atilde;o para mobile, os controles do jogo foram simplificados para serem utilizados em uma tela de toque com bot&otilde;es virtuais na tela.&nbsp;</span></p><p><span style=\"font-size: 18px;\">Como a vers&atilde;o &eacute; diferente da original para consoles e PC, n&atilde;o ser&aacute; poss&iacute;vel enfrentar jogadores de outras plataformas nela. Em compensa&ccedil;&atilde;o, ser&atilde;o realizadas mudan&ccedil;as para oferecer uma experi&ecirc;ncia mobile mais divertida. Entre elas est&atilde;o novas lendas, mapas, eventos e modos de jogo exclusivos, como Mini Royale e outros modos por tempo limitado.&nbsp;</span></p><p><span style=\"font-size: 18px;\">O sistema de recompensas para aqueles que fizerem o pr&eacute;-registro ir&aacute; oferecer itens cosm&eacute;ticos de acordo com a quantidade de usu&aacute;rios que se inscreverem. Atualmente, mais de 7,5 milh&otilde;es est&atilde;o aguardando o lan&ccedil;amento. At&eacute; agora, os jogadores receber&atilde;o uma Ins&iacute;gnia de Fundador, itens da lenda Bloodhound, como um moldura de banner e pose de banner, e um visual &eacute;pico para a arma R99. Se o numero de pr&eacute;-registro ultrapassar 10 milh&otilde;es de usu&aacute;rios, ser&aacute; desbloqueado o visual &eacute;pico de Bloodhound.</span>&nbsp;</p> ', ' app/controllers/images/carousel/imagem_2022-03-18_140626.png'),
(' Ford passa a usar robôs para operar impressoras 3', ' Tecnologias em Geral', ' rickzinho', '2022-03-18 14:08:53', ' <p><span style=\"font-size: 18px;\">A Ford desenvolveu um sistema para permitir que rob&ocirc;s utilizados na linha de produ&ccedil;&atilde;o da montadora possam operar impressoras 3D sem ajuda humana. Javier, nome dado ao rob&ocirc; com rodas da fornecedora KUKA, pode executar a impressora Carbon 3D continuamente, sem que um funcion&aacute;rio da empresa precise observar ou intervir.&nbsp;</span></p><p><span style=\"font-size: 18px;\">&quot;O Javier &eacute; encarregado de operar as impressoras 3D completamente por conta pr&oacute;pria&rdquo;, disse a Ford em comunicado reportado pelo&nbsp;TechCrunch. &ldquo;Ele &eacute; sempre pontual, muito preciso em seus movimentos e trabalha a maior parte do dia &ndash; fazendo apenas uma pequena pausa para recarregar&quot;.&nbsp;</span></p><p><span style=\"font-size: 18px;\">Quando a impressora Carbon 3D informa a Javier que o componente impresso do carro est&aacute; pronto, Javier o recupera e o coloca de lado para que um funcion&aacute;rio colete a pe&ccedil;a mais tarde.&nbsp;A montadora afirmou tamb&eacute;m que os rob&ocirc;s est&atilde;o constantemente aprendendo com os dados da impressora para ajudar a obter mais precis&atilde;o e reduzir as margens de erro.&nbsp;</span></p><p><span style=\"font-size: 18px;\">O novo sistema j&aacute; ajudou a produzir pe&ccedil;as automotivas personalizadas de baixo volume, como o suporte da linha de freio para os carros esportivos Mustang Shelby GT500.&nbsp;</span></p><p><span style=\"font-size: 18px;\">Geralmente, equipamentos de fornecedores diferentes n&atilde;o conseguem interagir porque usam interfaces de comunica&ccedil;&atilde;o separadas, por isso o sistema da Ford abre possibilidades &nbsp;para que outras m&aacute;quinas envolvidas no processo de produ&ccedil;&atilde;o colaborem. A novidade est&aacute; sendo implementada no Centro de Manufatura Avan&ccedil;ada da Ford, em Redford, nos Estados Unidos.&nbsp;</span></p><p><span style=\"font-size: 18px;\">&ldquo;Esse novo processo tem a capacidade de mudar a maneira como usamos a rob&oacute;tica em nossas instala&ccedil;&otilde;es de fabrica&ccedil;&atilde;o&rdquo;, disse o diretor de desenvolvimento global de tecnologia de fabrica&ccedil;&atilde;o,&nbsp;Jason Ryska.&nbsp;</span></p><p><span style=\"font-size: 18px;\">A Ford registrou patentes para as interfaces de comunica&ccedil;&atilde;o e tamb&eacute;m para o posicionamento preciso dos rob&ocirc;s. Embora o processo seja aut&ocirc;nomo, operadores devem carregar os projetos 3D na impressora e fazer a manuten&ccedil;&atilde;o do maquin&aacute;rio.</span>&nbsp;</p> ', ' app/controllers/images/carousel/imagem_2022-03-18_140852.png'),
(' E isso é tudo pessoal!', ' Tecnologias em Geral', ' rickzinho', '2022-03-18 14:09:43', ' <p>:D</p> ', ' app/controllers/images/carousel/imagem_2022-03-18_140933.png');